mapper.update:
update ims_ewei_shop_member_log
set status = #{status}
where id = #{id}

mapper.select:
SELECT l.id, m.uid, m.avatar, m.nickname, l.createtime, l.`status`, l.money, u.phone as mobile FROM ims_ewei_shop_member_log l LEFT JOIN ims_ewei_shop_member m LEFT JOIN ims_users u ON m.uid = u.uid ON l.openid = m.openid WHERE l.type = 1
and m.mobile = #{phone}
limit #{page},#{limit}
