mapper.select:
SELECT SUM(money) FROM t_member_upgrade_statistics
WHERE PERIOD_DIFF(date_format(now() , '%Y%m' ),date_format(statistics_date, '%Y%m')) =1
AND city = #{city}

SELECT SUM(money) FROM t_member_upgrade_statistics
WHERE PERIOD_DIFF(date_format(now() , '%Y%m' ),date_format(statistics_date, '%Y%m')) =1
AND city = #{city} and circle = #{circle}
